type Props = {
  name: IconType;
  className?: string;
  width?: number;
  height?: number;
};

export default function Icon({ name, className, width, height }: Props) {
  width = width || 16;
  height = height || 16;

  // if className has something w- or h- in it, use that instead
  const widthClass = className?.match(/w-\d+/g);
  const heightClass = className?.match(/h-\d+/g);

  if (widthClass) {
    width = parseInt(widthClass[0].split("-")[1]) * 4;
  }

  if (heightClass) {
    height = parseInt(heightClass[0].split("-")[1]) * 4;
  }

  return (
    <div
      style={{
        width: width,
        height: height,
      }}
      className={`flex items-center justify-center ${className}`}
    >
      {name === "airplane" && <Airplane />}
      {name === "behance" && <Behnace />}
      {name === "arrow-down" && <ArrowDown />}
      {name === "arrow-left" && <ArrowLeft />}
      {name === "logo" && <Logo />}
      {name === "arrow-right" && <ArrowRight />}
      {name === "circle-cross" && <CircleCross />}
      {name === "copied" && <Copied />}
      {name === "cross" && <Cross />}
      {name === "danger" && <Danger />}
      {name === "download" && <Download />}
      {name === "email" && <Email />}
      {name === "hamburger" && <Hamburger />}
      {name === "location-pin" && <LocationPin />}
      {name === "lock" && <Lock />}
      {name === "high-signal" && <HighSignal />}
      {name === "med-signal" && <MedSignal />}
      {name === "low-signal" && <LowSignal />}
      {name === "pause" && <Pause />}
      {name === "phone" && <Phone />}
      {name === "play" && <Play />}
      {name === "print" && <Print />}
      {name === "tick" && <Tick />}
      {name === "linkedin" && <Linkedin />}
      {name === "twitter" && <Twitter />}
      {name === "spotify" && <Spotify />}
      {name === "instagram" && <Instagram />}
      {name === "facebook" && <Facebook />}
      {name === "check" && <Check />}
    </div>
  );
}
